
<template>
	<!-- Main Sidebar -->
	<a-layout-sider
		class="sider-primary"
		breakpoint="lg"
		collapsed-width="0"
		width="250px"
		:collapsed="sidebarCollapsed"
		@collapse="$emit('toggleSidebar', ! sidebarCollapsed)"
		:class="['ant-layout-sider-' + sidebarColor, 'ant-layout-sider-' + sidebarTheme]"
		theme="light"
		:style="{ backgroundColor: 'transparent' }"
	>
		<!-- Brand Logo -->
		<div class="brand logo-cotainer">
			<img src="images/logo-ct-black.png" alt="">
			<span>Setup</span>
		</div>
		<hr />

		<!-- Sidebar Navigation Menu -->
		<a-menu theme="light" mode="inline">
			<template v-for="(item, index) in menuItems">
				<a-menu-item :key="index" v-if="!item.header">
					<router-link :to="item.route">
						<span class="icon" v-html="item.icon"></span>
						<span class="label">{{ item.label }}</span>
					</router-link>
				</a-menu-item>
				<a-menu-item class="menu-item-header" v-else>
					{{ item.label }}
				</a-menu-item>
			</template>
		</a-menu>
		<!-- / Sidebar Navigation Menu -->

		<!-- Sidebar Footer -->
		<div class="aside-footer">
			<div class="footer-box">
				<span class="icon">
					<svg width="20" height="20" fill="none" xmlns="http://www.w3.org/2000/svg">
						<path d="M3 4C3 3.44772 3.44772 3 4 3H16C16.5523 3 17 3.44772 17 4V6C17 6.55228 16.5523 7 16 7H4C3.44772 7 3 6.55228 3 6V4Z" fill="#111827"/>
						<path d="M3 10C3 9.44771 3.44772 9 4 9H10C10.5523 9 11 9.44771 11 10V16C11 16.5523 10.5523 17 10 17H4C3.44772 17 3 16.5523 3 16V10Z" fill="#111827"/>
						<path d="M14 9C13.4477 9 13 9.44771 13 10V16C13 16.5523 13.4477 17 14 17H16C16.5523 17 17 16.5523 17 16V10C17 9.44771 16.5523 9 16 9H14Z" fill="#111827"/>
					</svg>
				</span>
				<span>
					<p style="margin: 0 !important; text-overflow: ellipsis; width: 80px; text-wrap: nowrap;">I'm Userdwendewjfd</p>
					<p  style="font-size: 10px; margin: 0 !important; text-overflow: ellipsis; width: 80px; text-wrap: nowrap;">admin</p>
				</span>
				
			</div>
		</div>
		<!-- / Sidebar Footer -->

	</a-layout-sider>
</template>

